StructuralRule.Predicate:
FunctionCall call: call.function.name matches "(?i)set_Algorithm" and
    call.instance is [Location loc: loc.type.name matches "(?i)HashedData"] and
    (
        call.arguments[0] is [VariableAccess va: va.variable.name matches "(?i)CAPICOM_HASH_ALGORITHM_(MD2|MD4|MD5|SHA1)"] or
        /* SHA1 */
        call.arguments[0].constantValue == 0 or
        /* MD2 */
        call.arguments[0].constantValue == 1 or
        /* MD4 */
        call.arguments[0].constantValue == 2 or
        /* MD5 */
        call.arguments[0].constantValue == 3
    )

FunctionCall call: call.function.name matches "(?i)set_Name" and
    call.instance is [FieldAccess fa: fa.field.name matches "(?i)Algorithm"] and
    (
        call.arguments[0] is [VariableAccess va: va.variable.name matches "(?i)CAPICOM_ENCRYPTION_ALGORITHM_(RC4|DES|3DES)"] or
        /* RC4 */
        call.arguments[0].constantValue == 1 or
        /* DES */
        call.arguments[0].constantValue == 2 or
        /* 3DES */
        call.arguments[0].constantValue == 3
    )
